/* أضف كود CSS المخصص هنا */

.custom-button {
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  border: none;
  border-radius: 25px;
  padding: 1rem 2rem;
  color: white;
  font-weight: bold;
  transition: transform 0.3s ease;
}

.custom-button:hover {
  transform: translateY(-2px);
}

